import React, { useEffect, useState } from "react";
import useCart from "../../hooks/useCart";
import { deleteFromLocal } from "../../LocalDb/LocalDb";
import CartSingleProduct from "../CartSingleProduct/CartSingleProduct";
import SummaryCart from "../SummaryCart/SummaryCart";
import "./Cart.css";

const Cart = () => {
  const [allProducts, setAllProducts] = useState([]);
  useEffect(() => {
    axios.get("http://localhost:5000/digitalTechnologies/all").then((res) => {
      setAllProducts(res.data);
    });
  }, []);

  const [cart, setCart] = useCart(allProducts);

  const handleRemove = (id) => {
    const remainingProducts = cart.filter((product) => product._id !== id);
    setCart(remainingProducts);
    deleteFromLocal(id);
  };
