            key={product._id}
            item={product}
          ></CartSingleProduct>
        ))}
      </div>
      <div className="summary-container">
        <SummaryCart
          cart={cart}
          // handleBuy={handleBuy}
        ></SummaryCart>
      </div>
    </div>
  );
};

export default Cart;
