import axios from "axios";
import React, { useEffect, useState } from "react";
import { Card, Container } from "react-bootstrap/esm";
import { useParams } from "react-router-dom";
import Footer from "../Footer/Footer";
const KhoborDetails = () => {
  const [khobor, setKhobor] = useState({});
  const [allNews, setAllNews] = useState([]);
  const { id } = useParams();
  const { title, _id, picture, des } = khobor || {};
  const [showLoader, setShowLoader] = useState(true);

  useEffect(() => {
    axios.get(`http://localhost:5000/recentNews/${id}`).then((res) => {
      setKhobor(res.data);
      console.log(res.data);
    });

    axios.get(`http://localhost:5000/recentNews`).then((res) => {
      setAllNews(res.data);
      //   setShowLoader(false);
      console.log(res.data);
    });
  }, [id]);

  return (
    <>
      <Container style={{ padding: "24px 0" }}>
        <Card style={{ border: "none" }}>
          <Card.Title style={{ margin: "30px", fontSize: "40px" }}>
            {title}
          </Card.Title>
          <Card.Img width="400" height="385" variant="top" src={picture} />
          <Card.Body>
            <Card.Text
              style={{
                margin: "20px",
                fontSize: "22px",
                lineHeight: 1.8,
                color: "#666",
              }}
            >
              {des}
            </Card.Text>
          </Card.Body>
        </Card>
      </Container>
      <Container
        style={{ marginTop: "50px", borderRadius: "20px", padding: "6rem 0" }}
      >
        <Row>
          <Col xs={12} md={8} lg={8}>
            <h3
              style={{
                padding: "0.25rem 0.5rem",
                color: "white",
                backgroundColor: "#4db2ec",
              }}
              className="CardHeadline"
            >
              RELATED ARTICLES
            </h3>
            <Row>
              {allNews
                .filter((news) => news._id !== id)
                .map(({ title, picture, des, _id }) => {
                  return (
                    <Card
                      key={_id}
                      style={{
                        width: "14rem",
                        height: "16rem",
                        margin: "10px",
                        borderColor: "white",
                      }}
                    >
                      <Link to={`/recentNews/${_id}`}>
                        <div className="image-box">
                          <Card.Img
                            className="image"
                            variant="top"
                            src={picture}
                          ></Card.Img>
                        </div>
                        <Card.Body>
                          <Card.Title className="newTitle">{title}</Card.Title>
                        </Card.Body>
                      </Link>
                    </Card>
                  );
                })}
            </Row>
          </Col>
          <Motamot />
        </Row>
      </Container>
      <Footer />
    </>
  );
};

export default KhoborDetails;
