import React, { useContext } from "react";
import { Button } from "react-bootstrap";
import { Link } from "react-router-dom";
import { RemoveContext } from "../../router/AppRoute";

import "./SummaryCart.css";

const SummaryCart = ({ cart }) => {
  const [a, handleBuy] = useContext(RemoveContext);

  let productTotal = 0;
  let grandTotal = 0;
  let deliveryFee = 0;

  for (const product of cart) {
    console.log(product.price);
    const productPrice = product.price.split("");
    for (let i = 0; i < convertedProductPrice.length; i++) {
      // console.log(convertedProductPrice[i]);
      if (convertedProductPrice[i] === "০") {
        convertedProductPrice[i] = "0";
      } else if (convertedProductPrice[i] === "১") {
        convertedProductPrice[i] = "1";
      } else if (convertedProductPrice[i] === "২") {
        convertedProductPrice[i] = "2";
      } else if (convertedProductPrice[i] === "৩") {
        convertedProductPrice[i] = "3";
      } else if (convertedProductPrice[i] === "৪") {
        convertedProductPrice[i] = "4";
      } else if (convertedProductPrice[i] === "5") {
        convertedProductPrice[i] = "5";
      } else if (convertedProductPrice[i] === "৬") {
        convertedProductPrice[i] = "6";
      } else if (convertedProductPrice[i] === "৬") {
        convertedProductPrice[i] = "6";
      } else if (convertedProductPrice[i] === "৭") {
        convertedProductPrice[i] = "7";
      } else if (convertedProductPrice[i] === "৮") {
        convertedProductPrice[i] = "8";
      } else if (convertedProductPrice[i] === "৯") {
        convertedProductPrice[i] = "9";
      }
    }

    // const productPriceInNumber = Number(convertedProductPrice.join(""));
    // productTotal = productTotal + productPriceInNumber;
    // deliveryFee = productTotal > 0 ? 20 : 0;
    // grandTotal = productTotal + deliveryFee;
  }

  return (
    <div className="total-container p-4 text-start">
      <h4 className="fw-bold">Order Summary</h4>
      <hr className="px-2 text-secondary" />
      <div className=" text-secondary d-flex justify-content-between align-items-center fw-bold">
        <span>Total Products:</span>
        <span>{cart.length}</span>
      </div>
      <hr className="px-2 text-secondary" />
      <div className=" text-secondary d-flex justify-content-between align-items-center fw-bold">
        <span>Product Fee:</span>
        <span>{productTotal} BDT</span>
      </div>
      <hr className="px-2 text-secondary " />
      <div className="text-secondary d-flex justify-content-between align-items-center fw-bold">
        <span>Delivery Fee:</span>
        <span>{deliveryFee} BDT</span>
      </div>
      <hr className="px-2 text-secondary" />
      <div className=" text-dark d-flex justify-content-between align-items-center">
        <h4 className="fw-bold">Total:</h4>
        <h4 className="fw-bold">{grandTotal} BDT</h4>
      </div>
      <div className=" d-grid mt-1">
        <Link to={`/payment`} style={{ textDocoration: "none", color: "#fff" }}>
          <Button variant="success" style={{ marginRight: "20px" }}>
            Buy Now
          </Button>
        </Link>
      </div>
    </div>
  );
};

export default SummaryCart;
