import React, { useContext } from "react";
import { RemoveContext } from "../../router/AppRoute";

import "./SummaryCart.css";

const SummaryCart = ({ cart }) => {
  const [a, handleBuy] = useContext(RemoveContext);

  let productTotal = 0;
  let grandTotal = 0;
  let deliveryFee = 0;

  for (const product of cart) {
    const productPrice = product.price.split(" ").pop();
    const productPriceInBangla = productPrice.split(",").join("").split();
    // console.log(productPriceInBangla);
    for (let i = 0; i < productPriceInBangla.length; i++) {
      console.log(productPriceInBangla[i]);
      let priceInEng = [];

      if (productPriceInBangla[i] === "0") {
        productPriceInBangla[i] = "0";
      } else if (productPriceInBangla[i] === "১") {
        productPriceInBangla[i] = "1";
      } else if (productPriceInBangla[i] === "২") {
        productPriceInBangla[i] = "2";
      } else if (productPriceInBangla[i] === "৩") {
        productPriceInBangla[i] = "3";
      } else if (productPriceInBangla[i] === "৪") {
        productPriceInBangla[i] = "4";
      } else if (productPriceInBangla[i] === "5") {
        productPriceInBangla[i] = "5";
      } else if (productPriceInBangla[i] === "৬") {
        productPriceInBangla[i] = "6";
      } else if (productPriceInBangla[i] === "৬") {
        productPriceInBangla[i] = "6";
      } else if (productPriceInBangla[i] === "৭") {
        productPriceInBangla[i] = "7";
      } else if (productPriceInBangla[i] === "৮") {
        productPriceInBangla[i] = "8";
      } else if (productPriceInBangla[i] === "৯") {
        productPriceInBangla[i] = "9";
      }
    }
    productTotal = productTotal + productPrice;
    deliveryFee = productTotal > 0 ? 20 : 0;
    grandTotal = productTotal + deliveryFee;
  }

  return (
    <div className="total-container p-4 text-start">
      <h4 className="fw-bold">Order Summary</h4>
      <hr className="px-2 text-secondary" />
      <div className=" text-secondary d-flex justify-content-between align-items-center fw-bold">
        <span>Total Products:</span>
        <span>{cart.length}</span>
      </div>
      <hr className="px-2 text-secondary" />
      <div className=" text-secondary d-flex justify-content-between align-items-center fw-bold">
        <span>Product Fee:</span>
        <span>${productTotal}</span>
      </div>
      <hr className="px-2 text-secondary " />
      <div className="text-secondary d-flex justify-content-between align-items-center fw-bold">
        <span>Delivery Fee:</span>
        <span>${deliveryFee}</span>
      </div>
      <hr className="px-2 text-secondary" />
      <div className=" text-dark d-flex justify-content-between align-items-center">
        <h4 className="fw-bold">Total:</h4>
        <h4 className="fw-bold">${grandTotal}</h4>
      </div>
      <div className=" d-grid mt-1">
        <button className="btn btn-primary " onClick={handleBuy}>
          Buy Now
        </button>
      </div>
    </div>
  );
};

export default SummaryCart;
