import axios from "axios";
import React, { useEffect, useState } from "react";
import "./DashProfile.css";

const DashProfile = (props) => {
  const { user } = props;
  const [data, setData] = useState("");
  const [userDetails, setUserDetails] = useState({});
  console.log(userDetails);

  useEffect(() => {
    const url = `http://localhost:5000/users/${user.email}`;
    axios.get(url).then((res) => {
      setUserDetails(res.data[0]);
    });
  }, [user.email]);

  return (
    <div
      style={{
        display: "flex",
        justifyContent: "center",
        alignItems: "center",
      }}
    >
      <div>
        <div>
          <img className="userAvatar" src={user.imgUrl} alt="" />
        </div>
        <p style={{ lineHeight: "1.5", fontSize: "1.3rem" }}>
          Name: {userDetails.displayName}
        </p>
        <p>Email: {userDetails.email}</p>
        <p>Phone: {userDetails.mobile ? user.mobile : "01876980021"}</p>
        <p style={{ marginBottom: "0.5rem" }}>
          Location : {userDetails.location}
        </p>
        <div className="location-container">
          <h4>${data?.name}</h4>
          <h3>
            <span>${data?.main?.temp - 273.15}</span>
            &deg;C
          </h3>
        </div>
      </div>
    </div>
  );
};

export default DashProfile;
