import axios from "axios";
import React, { useEffect, useState } from "react";
import "./DashProfile.css";

const DashProfile = (props) => {
  const { user } = props;
  const [weatherInfo, setWeatherInfo] = useState("");
  console.log(user);

  useEffect(() => {
    axios
      .get(
        `https://api.openweathermap.org/data/2.5/weather?q=${props.user.location}&appid=2a67c3797591a5470e6cb9ae433854e7`
      )
      .then((res) => {
        setWeatherInfo(res.data);
      });
  }, []);

  return (
    <div
      style={{
        display: "flex",
        justifyContent: "center",
        alignItems: "center",
      }}
    >
      <div>
        <div>
          <img className="userAvatar" src={user.imgUrl} alt="" />
        </div>
        <p style={{ lineHeight: "1.5", fontSize: "1.3rem" }}>
          Name: {user.displayName}
        </p>
        <p>Email: {user.email}</p>
        <p>Phone: {user.mobile ? user.mobile : "01876980021"}</p>
        <p style={{ marginBottom: "0.5rem" }}>Location : {user.location}</p>
        <div className="location-container">
          <h1>${weatherInfo ? weatherInfo.name : null}</h1>
          {/* <h3>
            <span>${celcius}</span>&deg;C
          </h3> */}
          <h1 class="lead">
            ${weatherInfo ? weatherInfo.weather[0].main : null}
          </h1>
        </div>
      </div>
    </div>
  );
};

export default DashProfile;
