import axios from "axios";
import React, { useEffect, useState } from "react";
import "./DashProfile.css";

const DashProfile = (props) => {
  const { user } = props;
  const [data, setData] = useState("");
  const [userDetails, setUserDetails] = useState({});
  console.log(userDetails);

  useEffect(() => {
    const url = `http://localhost:5000/users/${user.email}`;
    axios.get(url).then((res) => {
      setUserDetails(res.data[0]);
    });

    axios
      .get(
        `https://api.openweathermap.org/data/2.5/weather?q=${userDetails?.location}&appid=2a67c3797591a5470e6cb9ae433854e7`
      )
      .then((res) => {
        setData(res.data);
      });
  }, [user.email, userDetails]);

  return (
    <div
      style={{
        display: "flex",
        justifyContent: "center",
        alignItems: "center",
      }}
    >
      <div>
        <div>
          <img className="userAvatar" src={userDetails.imgUrl} alt="" />
        </div>
        <p style={{ lineHeight: "1.5", fontSize: "1.3rem" }}>
          Name: {userDetails.displayName}
        </p>
        <p>Email: {userDetails.email}</p>
        <p>Phone: {userDetails.mobile}</p>
        <p style={{ marginBottom: "0.5rem" }}>
          Location : {userDetails.location}
        </p>
        <div className="location-container">
          <p style={{ marginBottom: "0.5rem" }}>
            <span>Temparature: {data?.main?.temp - 273.15}</span>
            &deg;C
          </p>
        </div>
      </div>
    </div>
  );
};

export default DashProfile;
