import React from "react";
import { getStoredData } from "../../LocalDb/LocalDb";
import CartSingleProduct from "../CartSingleProduct/CartSingleProduct";
import SummaryCart from "../SummaryCart/SummaryCart";

import "./Cart.css";

const Cart = ({ cart }) => {
  let items = 0;
  const savedItems = getStoredData();
  for (let items in savedItems) {
    items += 1;
  }

  console.log(items);
  return (
    <div className="cart-container  my-5 p-5">
      <div className="details-container">
        {cart?.map((product) => (
          <CartSingleProduct
            key={product._id}
            item={product}
          ></CartSingleProduct>
        ))}
      </div>
      <div className="summary-container">
        <SummaryCart
          cart={cart}
          // handleBuy={handleBuy}
        ></SummaryCart>
      </div>
    </div>
  );
};

export default Cart;
