import React from "react";
import { Card, Col, Container, Row } from "react-bootstrap/esm";
import { Link } from "react-router-dom";
import "./AgroGib.css";
import Records from "./AgroGib.json";

const AgroGib = () => {
  return (
    <Container style={{ marginTop: "50px", borderRadius: "20px" }}>
      <Row>
        <Col xs={12} md={8} lg={8}>
          <h3 className="CardHeadline">এগ্রো কমুনিটি</h3>
          <Row>
            {Records.map(({ title, picture, des, id }) => {
              return (
                <Card
                  key={id}
                  style={{
                    width: "14rem",
                    height: "16rem",
                    margin: "10px",
                    borderColor: "white",
                  }}
                >
                  <div className="image-box">
                    <Link to="/courseDetails">
                      <Card.Img
                        className="image"
                        variant="top"
                        src={picture}
                      ></Card.Img>
                      <Card.Body>
                        <Card.Title className="newTitle">{title}</Card.Title>
                      </Card.Body>
                    </Link>
                  </div>
                </Card>
              );
            })}
          </Row>
        </Col>
        <Col xs={4} md={4}>
          <h3 className="CardHeadline">মতামত</h3>
          {Records.map(({ title, des, picture }) => {
            return (
              <Card
                style={{
                  display: "flex",
                  alignItems: "center",
                  padding: "0.25rem",
                  flexDirection: "row",
                }}
              >
                <div className="card-img">
                  <img src={picture} style={{ width: "100%" }} />
                </div>
                <div>
                  <Link to="/motoamotDetials">
                    <Card.Body>
                      <Card.Title className="newTitle">{title}</Card.Title>
                    </Card.Body>
                  </Link>
                </div>
              </Card>
            );
          })}
        </Col>
      </Row>
    </Container>
  );
};

export default AgroGib;
